package org.firstinspires.ftc.teamcode.roadrunner.trajectorysequence.sequencesegment;import com.acmerobotics.roadrunner.geometry.Pose2d;import com.acmerobotics.roadrunner.trajectory.TrajectoryMarker;import org.firstinspires.ftc.teamcode.roadrunner.trajectorysequence.ActiveLinearAsyncManager;import java.util.List;/** * Marker that starts a function on another thread, the follower will only * wait (when waitForFinish==true) after its TrajectorySegment is completed */public class LinearAsyncMarker extends StationarySegment {    private String name;    private Runnable function;    public LinearAsyncMarker(String name, Runnable asyncAction, Pose2d pose, boolean keepPosition, List<TrajectoryMarker> markers) {        super("LINEAR_ASYNC_START", 0, pose, keepPosition, markers);        this.name = name;        this.function = asyncAction;    }    public boolean start() {        return ActiveLinearAsyncManager.add(name, function);    }    @Override    public Pose2d getPose(double time) {        return getStartPose();    }}